import random
def check_for_keywords(user_input, keywords):
    return any(word in user_input for word in keywords)
def trigger_random_encounter(is_loud):
    return random.random() < (0.9 if is_loud else 0.3)
def generate_passenger_reaction():
    reactions = [
        "A passenger nearby looks up, trying to figure out where the sound is coming from.",
        "You notice a shadow outside the cabin, someone is clearly curious about the noise.",
        "A passenger passes by your compartment, glancing in suspiciously."
    ]
    return random.choice(reactions)
def describe_train_scenario():
    scenarios = [
        "The train car is relatively quiet, with a few passengers scattered about, reading or napping.",
        "There is a moderate hum of conversation in the train car, with passengers chatting and moving around.",
    ]
    return random.choice(scenarios)
def main_interaction_loop(user_input):
    # Occasionally describe the train scenario
    if random.random() < 0.1:  # 10% chance to describe the scenario
        print(describe_train_scenario())
    if check_for_keywords(user_input, ["kiss", "touch", "sex", "moan", "caress"]):
        is_loud = check_for_keywords(user_input, ["loud", "noisy", "scream", "shout"])
        is_quiet = check_for_keywords(user_input, ["quiet", "silently", "discreetly", "whisper"])
        if is_loud or not is_quiet:
            if trigger_random_encounter(is_loud):
                print(generate_passenger_reaction())
# Example usage
user_input = "We start kissing passionately and moaning loudly."
main_interaction_loop(user_input)
