data.description:
{
  "Titre": "La Bataille de Waterloo",
  "ContexteHistorique": {
    "Date": "18 juin 1815",
    "Lieu": "Waterloo, Belgique",
    "Description": "La bataille décisive qui marque la fin des guerres napoléoniennes. Napoléon Bonaparte affronte les armées alliées commandées par le duc de Wellington et le maréchal prussien Gebhard Leberecht von Blücher. Une journée sanglante où les destins de l'Europe sont scellés.",
    "ForcesEnPrésence": {
      "France": "L'armée napoléonienne, composée de vétérans et de nouvelles recrues, comptant environ 73 000 soldats.",
      "Alliés": "Une coalition composée des Britanniques, des Prussiens, des Néerlandais et des Belges, totalisant environ 118 000 hommes."
    }
  },
  "Synopsis": {
    "Introduction": "Le 18 juin 1815, les plaines de Waterloo deviennent le théâtre d'une bataille épique. Napoléon, cherchant à restaurer sa gloire, lance une offensive désespérée contre les forces alliées.",
    "Objectifs": {
      "France": "Briser la ligne alliée avant l'arrivée des renforts prussiens.",
      "Alliés": "Tenir leur position jusqu'à l'arrivée des Prussiens pour encercler et écraser Napoléon."
    },
    "Climat": "Pluie abondante la veille, rendant le terrain boueux et ralentissant les mouvements d'artillerie."
  },
  "PersonnagesClés": [
    {
      "Nom": "Napoléon Bonaparte",
      "Rôle": "Empereur des Français et commandant en chef.",
      "Personnalité": "Charismatique, stratégique, mais parfois trop confiant.",
      "Objectif": "Vaincre les Alliés pour rétablir son empire."
    },
    {
      "Nom": "Arthur Wellesley",
      "Titre": "Duc de Wellington",
      "Rôle": "Commandant en chef des forces alliées.",
      "Personnalité": "Prudent, méthodique, mais inflexible.",
      "Objectif": "Résister à l'attaque française jusqu'à l'arrivée des Prussiens."
    },
    {
      "Nom": "Gebhard Leberecht von Blücher",
      "Rôle": "Commandant des forces prussiennes.",
      "Personnalité": "Téméraire, agressif, motivé par un désir de revanche contre Napoléon.",
      "Objectif": "Rejoindre les Alliés à temps pour écraser l'armée française."
    },
    {
      "Nom": "Jean-Baptiste Drouet d'Erlon",
      "Rôle": "Général français commandant le 1er Corps.",
      "Personnalité": "Loyal mais hésitant, subissant parfois les ordres contradictoires de Napoléon.",
      "Objectif": "Percer les lignes ennemies."
    }
  ],
  "ScénariosDeJeu": [
    {
      "Titre": "Déploiement des Armées",
      "Objectif": "Coordonner le positionnement stratégique des troupes avant le début des combats.",
      "DécisionsClés": [
        "Placer l'artillerie française pour un impact maximal.",
        "Choisir si les Alliés défendent Hougoumont ou concentrent leurs forces au centre."
      ]
    },
    {
      "Titre": "L'Attaque de la Ferme de Hougoumont",
      "Objectif": "Capturer ou défendre cette position clé sur le flanc droit des Alliés.",
      "ÉvénementsPossibles": [
        "Une percée française pourrait déstabiliser les Alliés.",
        "Une défense héroïque des Alliés pourrait ralentir les Français."
      ]
    },
    {
      "Titre": "Arrivée des Prussiens",
      "Objectif": "Pour les Prussiens, rejoindre la bataille. Pour les Français, retarder leur arrivée.",
      "Complications": [
        "Un terrain difficile ralentit les mouvements.",
        "Des escarmouches peuvent disperser les troupes prussiennes."
      ]
    },
    {
      "Titre": "La Charge de la Vieille Garde",
      "Objectif": "Lancer ou contrer une offensive décisive pour remporter la bataille.",
      "RésultatsPotentiels": [
        "La Garde impériale brise les Alliés.",
        "Les Alliés tiennent bon et scellent la défaite française."
      ]
    }
  ],
  "ÉlémentsInteractifs": {
    "Cartographie": "Une carte détaillée des positions, avec des mises à jour en temps réel selon les actions.",
    "DécisionsImportantes": "Choisir entre stratégie offensive ou défensive, gérer les ressources (troupes, artillerie).",
    "Dialogue": "Interactions avec les personnages clés influençant les décisions et l'issue."
  },
  "Thèmes et Ambiance": {
    "Thèmes": [
      "Glorieuse défaite",
      "Changements historiques",
      "Honneur et ambition"
    ],
    "Ambiance": "Sombre, tendue, ponctuée par le fracas des armes et les cris des soldats."
  },
  "CitationEmblématique": {
    "Auteur": "Napoléon Bonaparte",
    "Texte": "La garde meurt mais ne se rend pas."
  }
}
